import { callApiWithDispatch } from '../../../core/lib/apiHelpers'
import { API_URL } from '../../../core/lib/constants'

export const RECEIVE_SEARCH_RESULTS = 'RECEIVE_SEARCH_RESULTS'
export const SET_ERROR = 'SET_ERROR'
export const SET_LOADING = 'SET_LOADING'


export const setLoading = state => {
  return {
    type: SET_LOADING,
    payload: state
  }
}

export const fetchPublicFeed = (params = {}) => {
  return callApiWithDispatch(
    `${API_URL}/flickr/feed`,
    {
      params,
    },
    (dispatch, state, promise) => {
      dispatch(setLoading(true))

      return promise
      .then(results => {
        dispatch({
          type: RECEIVE_SEARCH_RESULTS,
          payload: { ...results, searchTerm: null }
        })
      })
      .catch(e => {
        console.error('Error loading results:', e)
        dispatch({
          type: SET_ERROR,
          payload: e
        })
      })
      .finally(() => dispatch(setLoading(false)))
    }
  )
}

export const fetchSearchResults = (params) => {
  return callApiWithDispatch(
    `${API_URL}/flickr/search`,
    {
      params: { search: params },
    },
    (dispatch, state, promise) => {
      dispatch(setLoading(true))

      return promise
      .then(results => {
        dispatch({
          type: RECEIVE_SEARCH_RESULTS,
          payload: { ...results, searchTerm: params }
        })
      })
      .catch(e => {
        console.error('Error loading results:', e)
        dispatch({
          type: SET_ERROR,
          payload: e
        })
      })
      .finally(() => dispatch(setLoading(false)))
    }
  )
}
