/*
 * A container component that wraps the search bar and supplies the dispatcher for submitting
 * the Flickr search. This is in order to make the component itself reusable and free from logic.
 */

import { connect } from 'react-redux'
import Component from '../components/SearchBar'
import { fetchSearchResults } from '../actions/imageSearchActions'
import { APIS } from '../../../core/lib/constants'

const mapStateToProps = (state, ownProps) => {
  return {
    ...ownProps,
  }
}

const mapDispatchToProps = (dispatch, ownProps) => {
  return {
    onSubmit: options => dispatch(fetchSearchResults(APIS.FLICKR, options)),
  }
}

const FlickrSearchForm = connect(
  mapStateToProps,
  mapDispatchToProps,
)(Component)

export default FlickrSearchForm
