/*
 * An image component meant to be displayed in the search results gallery.
 * It will be rendered as a link if a link URL is supplied.
 */

import PropTypes from 'prop-types'
import React, { PureComponent } from 'react'

class ImageThumbnail extends PureComponent {
  static propTypes = {
    author: PropTypes.string,
    className: PropTypes.string,
    date: PropTypes.string,
    link: PropTypes.string,
    onLoad: PropTypes.func,
    id: PropTypes.string,
    title: PropTypes.string,
    url: PropTypes.string.isRequired,
  }

  constructor(props) {
    super(props)

    this.handleLoaded = this.handleLoaded.bind(this)
  }

  /*
   * If supplied, this method will get called once the image has finished loading. For instance,
   * we may want to wait until all images have finished loading before rendering them.
   */
  handleLoaded() {
    if (this.props.onLoad) this.props.onLoad(this.props.id)
  }

  render() {
    const { className, link, title, url } = this.props
    const image = <img src={url} alt={title} onLoad={this.handleLoaded} />

    const content = !link
      ? image
      : (<a href={link} rel="noopener noreferrer" target="_blank">
          {image}
        </a>)

    return (
      <div className={className}>
        {content}
      </div>
    )
  }
}

export default ImageThumbnail
